import React from 'react';
import LoginForm from './components/LoginForm';
import TaskList from './components/TaskList';
import TaskModal from './components/TaskModal';

const App = () => {
    const [loggedIn, setLoggedIn] = React.useState(false);
    const [tasks, setTasks] = React.useState([]);
    const [showModal, setShowModal] = React.useState(false);

    const handleLogin = (username, password) => {
        // Отправить запрос на аутентификацию пользователя
        // Если успешно, установить loggedIn в true
        setLoggedIn(true);
    };

    const handleLogout = () => {
        // Реализация выхода из системы
        setLoggedIn(false);
    };

    const handleFetchTasks = () => {
        // Отправить запрос на получение списка задач
        // Установить полученные задачи в состояние
        const dummyTasks = [{ id: 1, title: 'Task 1', description: 'Description of Task 1', due_date: '2024-02-29', priority: 'high', status: 'todo' }];
        setTasks(dummyTasks);
    };

    React.useEffect(() => {
        if (loggedIn) {
            handleFetchTasks();
        }
    }, [loggedIn]);

    return (
        <div>
            {loggedIn ? (
                <>
                    <button onClick={handleLogout}>Logout</button>
                    <TaskList tasks={tasks} />
                    <button onClick={() => setShowModal(true)}>New Task</button>
                    {showModal && <TaskModal onClose={() => setShowModal(false)} />}
                </>
            ) : (
                <LoginForm onLogin={handleLogin} />
            )}
        </div>
    );
};

export default App;
