import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';

const TaskPage = () => {
    const [tasks, setTasks] = useState([]);
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [selectedTask, setSelectedTask] = useState(null);
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        due_date: '',
        priority: '',
        status: '',
        creator_id: '',
        responsible_id: ''
    });

    useEffect(() => {
        fetchTasks();
    }, []);

    const fetchTasks = async () => {
        try {
            const response = await axios.get('http://localhost:8000/tasks');
            setTasks(response.data.tasks);
        } catch (error) {
            console.error('Failed to fetch tasks:', error);
        }
    };

    const openModal = (task) => {
        setSelectedTask(task);
        if (task) {
            setFormData({
                title: task.title,
                description: task.description,
                due_date: task.due_date,
                priority: task.priority,
                status: task.status,
                creator_id: task.creator_id,
                responsible_id: task.responsible_id
            });
        } else {
            setFormData({
                title: '',
                description: '',
                due_date: '',
                priority: '',
                status: '',
                creator_id: '',
                responsible_id: ''
            });
        }
        setModalIsOpen(true);
    };

    const closeModal = () => {
        setSelectedTask(null);
        setModalIsOpen(false);
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (selectedTask) {
                // Редактирование существующей задачи
                await axios.put(`http://localhost:8000/tasks/${selectedTask.id}`, formData);
            } else {
                // Создание новой задачи
                await axios.post('http://localhost:8000/tasks', formData);
            }
            closeModal();
            fetchTasks();
        } catch (error) {
            console.error('Failed to save task:', error);
        }
    };

    return (
        <div>
            <h2>Новая задача</h2>
            {error && <div style={{ color: 'red' }}>{error}</div>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Заголовок:</label>
                    <input type="text" name="title" value={title} onChange={(e) => setTitle(e.target.value)} />
                </div>
                <div>
                    <label>Описание:</label>
                    <textarea name="description" value={description} onChange={(e) => setDescription(e.target.value)} />
                </div>
                <div>
                    <label>Дата завершения:</label>
                    <input type="date" name="due_date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} />
                </div>
                <div>
                    <label>Приоритет:</label>
                    <select name="priority" value={priority} onChange={(e) => setPriority(e.target.value)}>
                        <option value="">Выберите приоритет</option>
                        <option value="высокий">Высокий</option>
                        <option value="средний">Средний</option>
                        <option value="низкий">Низкий</option>
                    </select>
                </div>
                <div>
                    <label>Статус:</label>
                    <select name="status" value={status} onChange={(e) => setStatus(e.target.value)}>
                        <option value="">Выберите статус</option>
                        <option value="к выполнению">К выполнению</option>
                        <option value="выполняется">Выполняется</option>
                        <option value="выполнена">Выполнена</option>
                        <option value="отменена">Отменена</option>
                    </select>
                </div>
                <div>
                    <label>Создатель:</label>
                    <input type="text" name="creator_id" value={creatorId} onChange={(e) => setCreatorId(e.target.value)} />
                </div>
                <div>
                    <label>Ответственный:</label>
                    <input type="text" name="responsible_id" value={responsibleId} onChange={(e) => setResponsibleId(e.target.value)} />
                </div>
                <button type="submit">Создать задачу</button>
            </form>
        </div>
    );
};

export default TaskPage;
