import React from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import TasksPage from './pages/TasksPage';

const App = () => {
    // Проверка, авторизован ли пользователь (можно добавить дополнительную логику)
    const isAuthenticated = false; // Здесь должна быть логика для проверки авторизации пользователя

    return (
        <Router>
            <Switch>
                <Route path="/login">
                    {isAuthenticated ? <Redirect to="/tasks" /> : <LoginPage />}
                </Route>
                <Route path="/tasks">
                    {isAuthenticated ? <TasksPage /> : <Redirect to="/login" />}
                </Route>
                <Redirect from="/" to="/login" />
            </Switch>
        </Router>
    );
};

ReactDOM.render(<App />, document.getElementById('root'));
